con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.responseContentAsXml)

def customerid = context.expand( '${GetCustomerById#Response#//*:id}' )

log.info "Customer ID is $customerid"

assert holder.getNodeValue("//*:id") == customerid

con:config.script:
// Get the Email by property expansion
def email = context.expand( '${SaveCustomer#Response#//*:email}' )

// Print the Email just so we can debug it
log.info "Email is $email"



// If user is not ExpertzMe, the next step to execute should be logout service
if(email == "sindolkamal@gmail.com" ){
log.info "Pass"
}
else{
	log.info "email entry wrong, try again! "
	testRunner.gotoStepByName("DeleteCustomer")
}

// Get the id by property expansion
def id = context.expand( '${SaveCustomer#Response#//*:id}' )

// Print the id just so we can debug it
log.info "ID is $id"

// Get the CustomerId by property expansion
def customerid = context.expand( '${GetCustomerById#Response#//*:id}' )

// Print the Customerid just so we can debug it
log.info "Customer ID is $customerid"

def email = context.expand( '${SaveCustomer#Response#//*:email}' )


// If user is not ExpertzMe, the next step to execute should be logout service
if(customerid == id  && email =="sindolkamal@gmail.com"){
log.info "Pass"
testRunner.gotoStepByName("GetCustomerList")
}
else{
// Execute the next step as normal
log.info "Please try again"
}

